    WITH OrderedTransactions AS (
        SELECT 
            "transaction_time",
            "transaction_amount",
            DATE("transaction_time") as transaction_date
        FROM 
            transactions
        WHERE
            "transaction_time" BETWEEN '2021-01-01' AND '2021-01-31'
    ),
    RollingAverage AS (
        SELECT 
            transaction_date,
            AVG("transaction_amount") OVER (
                ORDER BY transaction_date
                ROWS BETWEEN 2 PRECEDING AND CURRENT ROW
            ) AS rolling_avg_amount
        FROM 
            OrderedTransactions
    )
    SELECT DISTINCT
        transaction_date,
        rolling_avg_amount
    FROM 
        RollingAverage
    ORDER BY 
        transaction_date;

